  const formatDateTime = (
    date: Date | string | null,
    includeTime = false,
    forceUTC = false
  ): string => {
    if (!date) return "";

    const d = new Date(date);

    // Formateamos por partes para controlar exactamente salida (dd MMM yyyy[, HH:mm])
    const dateFmt = new Intl.DateTimeFormat("es-MX", {
      day: "2-digit",
      month: "short",
      year: "numeric",
      timeZone: forceUTC ? "UTC" : undefined,
    });

    const parts = dateFmt.formatToParts(d);
    const day = parts.find((p) => p.type === "day")?.value ?? "";
    let month = parts.find((p) => p.type === "month")?.value ?? "";
    const year = parts.find((p) => p.type === "year")?.value ?? "";

    // Normalizar abreviatura de mes a "sep" en vez de "sept." (si aparece)
    month = month.replace(/\./g, "").replace("sept", "sep").toLowerCase();

    let result = `${day} ${month} ${year}`;

    if (includeTime) {
      const timeParts = new Intl.DateTimeFormat("es-MX", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
        timeZone: forceUTC ? "UTC" : undefined,
      }).formatToParts(d);

      const hour = timeParts.find((p) => p.type === "hour")?.value ?? "00";
      const minute = timeParts.find((p) => p.type === "minute")?.value ?? "00";
      const dayPeriod = timeParts.find((p) => p.type === "dayPeriod")?.value ?? "";

      result += `, ${hour}:${minute} ${dayPeriod}`;
    }

    return result;
  };

  // Formatear fecha/hora de vuelo usando la zona horaria guardada o detectada
  const formatFlightDateTime = (
    date: Date | string | null,
    cityString: string | null,
    savedTimezone?: string | null
  ): string => {
    if (!date) return "";

    const d = new Date(date);

    // Determinar la zona horaria a usar
    let timezone = 'America/Mexico_City'; // Fallback por defecto

    if (savedTimezone) {
      // Usar la zona horaria guardada en la base de datos (mayor prioridad)
      timezone = savedTimezone;
    } else {
      // Si no hay zona horaria guardada, extraer código IATA y buscar
      const iataCode = extractIataCode(cityString || '');
      timezone = getTimezoneForAirport(iataCode, 'America/Mexico_City');
    }

    // Formatear fecha y hora en la zona horaria correcta
    const dateFmt = new Intl.DateTimeFormat("es-MX", {
      day: "2-digit",
      month: "short",
      year: "numeric",
      timeZone: timezone,
    });

    const parts = dateFmt.formatToParts(d);
    const day = parts.find((p) => p.type === "day")?.value ?? "";
    let month = parts.find((p) => p.type === "month")?.value ?? "";
    const year = parts.find((p) => p.type === "year")?.value ?? "";

    month = month.replace(/\./g, "").replace("sept", "sep").toLowerCase();

    const timeParts = new Intl.DateTimeFormat("es-MX", {
      hour: "2-digit",
      minute: "2-digit",
      hour12: true,
      timeZone: timezone,
    }).formatToParts(d);

    const hour = timeParts.find((p) => p.type === "hour")?.value ?? "00";
    const minute = timeParts.find((p) => p.type === "minute")?.value ?? "00";
    const dayPeriod = timeParts.find((p) => p.type === "dayPeriod")?.value ?? "";

    return `${day} ${month} ${year}, ${hour}:${minute} ${dayPeriod}`;
  };

  // Formatear hora de 24h (HH:mm) a 12h con AM/PM
  const formatTime12h = (time24: string | null): string => {
    if (!time24) return "N/A";

    const [hours, minutes] = time24.split(':').map(Number);
    const period = hours >= 12 ? 'p. m.' : 'a. m.';
    const hours12 = hours % 12 || 12;

    return `${hours12.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')} ${period}`;
  };

