import * as React from "react";
import { Check, ChevronsUpDown } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { TIMEZONE_CATALOG } from "@/lib/timezone-catalog";

interface TimezoneComboboxProps {
  value: string;
  onValueChange: (value: string) => void;
  placeholder?: string;
  id?: string;
  testId?: string;
  suggestedTimezones?: string[]; // Zonas horarias sugeridas del aeropuerto
}

export function TimezoneCombobox({
  value,
  onValueChange,
  placeholder = "Buscar zona horaria...",
  id,
  testId,
  suggestedTimezones = [],
}: TimezoneComboboxProps) {
  const [open, setOpen] = React.useState(false);

  // Encontrar el label de la zona horaria seleccionada
  const selectedLabel = React.useMemo(() => {
    if (!value) return null;
    for (const region of TIMEZONE_CATALOG) {
      const tz = region.timezones.find((t) => t.value === value);
      if (tz) return tz.label;
    }
    return null;
  }, [value]);

  // Crear lista de zonas sugeridas con sus labels
  const suggestedTimezoneOptions = React.useMemo(() => {
    if (!suggestedTimezones || suggestedTimezones.length === 0) return [];

    const options = [];
    for (const tzValue of suggestedTimezones) {
      for (const region of TIMEZONE_CATALOG) {
        const tz = region.timezones.find((t) => t.value === tzValue);
        if (tz) {
          options.push(tz);
          break;
        }
      }
    }
    return options;
  }, [suggestedTimezones]);

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          id={id}
          data-testid={testId}
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
        >
          {selectedLabel || placeholder}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-full p-0" align="start">
        <Command>
          <CommandInput placeholder="Buscar zona horaria..." />
          <CommandList>
            <CommandEmpty>No se encontró zona horaria.</CommandEmpty>

            {/* Mostrar zonas sugeridas del aeropuerto primero */}
            {suggestedTimezoneOptions.length > 0 && (
              <CommandGroup heading="🛫 Zonas del aeropuerto seleccionado" className="bg-blue-50/50">
                {suggestedTimezoneOptions.map((tz) => (
                  <CommandItem
                    key={`suggested-${tz.value}`}
                    value={tz.label}
                    onSelect={() => {
                      onValueChange(tz.value);
                      setOpen(false);
                    }}
                    className="font-medium"
                  >
                    <Check
                      className={cn(
                        "mr-2 h-4 w-4",
                        value === tz.value ? "opacity-100" : "opacity-0"
                      )}
                    />
                    {tz.label}
                  </CommandItem>
                ))}
              </CommandGroup>
            )}

            {/* Mostrar todas las zonas horarias por región */}
            {TIMEZONE_CATALOG.map((region) => (
              <CommandGroup key={region.region} heading={region.region}>
                {region.timezones.map((tz) => (
                  <CommandItem
                    key={tz.value}
                    value={tz.label}
                    onSelect={() => {
                      onValueChange(tz.value);
                      setOpen(false);
                    }}
                  >
                    <Check
                      className={cn(
                        "mr-2 h-4 w-4",
                        value === tz.value ? "opacity-100" : "opacity-0"
                      )}
                    />
                    {tz.label}
                  </CommandItem>
                ))}
              </CommandGroup>
            ))}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 * 
 * Features:
 * - Renders as a customizable button that opens a file upload modal
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 * All file management features are automatically handled by the Uppy dashboard modal.
 * 
 * @param props - Component props
 * @param props.maxNumberOfFiles - Maximum number of files allowed to be uploaded
 *   (default: 1)
 * @param props.maxFileSize - Maximum file size in bytes (default: 10MB)
 * @param props.onGetUploadParameters - Function to get upload parameters (method and URL).
 *   Typically used to fetch a presigned URL from the backend server for direct-to-S3
 *   uploads.
 * @param props.onComplete - Callback function called when upload is complete. Typically
 *   used to make post-upload API calls to update server state and set object ACL
 *   policies.
 * @param props.buttonClassName - Optional CSS class name for the button
 * @param props.children - Content to be rendered inside the button
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
      })
  );

  return (
    <div>
      <Button onClick={() => setShowModal(true)} className={buttonClassName}>
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

