import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Calendar,
  MapPin,
  Users,
  Bed,
  Camera,
  Plane,
  Car,
  Ship,
  Shield,
  StickyNote,
  Eye,
  EyeOff,
  FileText,
  ArrowLeft,
  Download,
  Mail,
  Globe,
} from "lucide-react";
import { AttachmentList } from "@/components/AttachmentLink";
import { extractIataCode, getTimezoneForAirport } from "@/lib/timezones";
import logoPng from "@assets/LOGO_PNG_NEGRO-min_1755552589565.png";

interface TravelData {
  travel: any;
  accommodations: any[];
  activities: any[];
  flights: any[];
  transports: any[];
  cruises: any[];
  insurances: any[];
  notes: any[];
}

export default function TravelPreview() {
  const { id } = useParams<{ id: string }>();

  // Obtener el token de los query parameters si existe
  const searchParams = new URLSearchParams(window.location.search);
  const publicToken = searchParams.get("token");

  const { data, isLoading, error } = useQuery<TravelData>({
    queryKey: ["/api/travels", id, "full", publicToken],
    queryFn: async () => {
      const url = new URL(`/api/travels/${id}/full`, window.location.origin);
      if (publicToken) {
        url.searchParams.set("token", publicToken);
      }
      const response = await fetch(url.toString());
      if (!response.ok) {
        throw new Error("Failed to fetch travel data");
      }
      return response.json();
    },
    enabled: !!id,
    staleTime: 1 * 60 * 1000, // 1 minute for preview (fresher data)
    refetchOnWindowFocus: true,
    refetchInterval: 2 * 60 * 1000, // Auto-refresh every 2 minutes for preview
  });

  const formatRoomType = (roomType: string): JSX.Element => {
    const [cantidad, ...tipoParts] = roomType.split(" ");
    const tipo = tipoParts.join(" ");

    return (
      <span>
        <strong>#</strong> {cantidad} <strong> - Tipo</strong> {tipo}
      </span>
    );
  };

  const formatPhoneNumber = (phoneNumber: string) => {
    return phoneNumber
      .replace(/\D/g, "")
      .replace(/^(\d{3})(\d{3})(\d{4})$/, "$1-$2-$3");
  };

  const formatDateTime = (dateTime: string | Date) => {
    const date = new Date(dateTime);
    return date.toLocaleString("es-ES", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      hour12: true, // 👈 Esto forza el formato AM/PM
    });
  };

  // Formatear fecha/hora de vuelo usando la zona horaria guardada o detectada
  const formatFlightDateTime = (dateTime: string | Date, cityString: string, savedTimezone?: string | null) => {
    const date = new Date(dateTime);

    // Determinar la zona horaria a usar
    let timezone = 'America/Mexico_City'; // Fallback por defecto

    if (savedTimezone) {
      // Usar la zona horaria guardada en la base de datos (mayor prioridad)
      timezone = savedTimezone;
    } else {
      // Si no hay zona horaria guardada, extraer código IATA y buscar
      const iataCode = extractIataCode(cityString || '');
      timezone = getTimezoneForAirport(iataCode, 'America/Mexico_City');
    }

    // Formatear en la zona horaria correcta
    return date.toLocaleString("es-ES", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      hour12: true,
      timeZone: timezone,
    });
  };

  // Formatear hora de 24h (HH:mm) a 12h con AM/PM
  const formatTime12h = (time24: string | null): string => {
    if (!time24) return "N/A";

    const [hours, minutes] = time24.split(':').map(Number);
    const period = hours >= 12 ? 'p. m.' : 'a. m.';
    const hours12 = hours % 12 || 12;

    return `${hours12.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')} ${period}`;
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString("es-ES", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const handlePrint = () => {
    window.print();
  };

  const handleGoBack = () => {
    window.close();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-accent mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando vista previa...</p>
        </div>
      </div>
    );
  }

  if (error || !data) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-foreground mb-2">Error</h1>
          <p className="text-muted-foreground">
            No se pudo cargar el itinerario
          </p>
        </div>
      </div>
    );
  }

  const {
    travel,
    accommodations,
    activities,
    flights,
    transports,
    cruises,
    insurances,
    notes,
  } = data;

  // Obtener fecha en zona horaria específica para agrupación
  const getDateInTimezone = (dateTime: string | Date, cityString: string, savedTimezone?: string | null): Date => {
    const date = new Date(dateTime);

    // Determinar la zona horaria a usar (misma lógica que formatFlightDateTime)
    let timezone = 'America/Mexico_City'; // Fallback por defecto

    if (savedTimezone) {
      // Usar la zona horaria guardada en la base de datos (mayor prioridad)
      timezone = savedTimezone;
    } else {
      // Si no hay zona horaria guardada, extraer código IATA y buscar
      const iataCode = extractIataCode(cityString || '');
      timezone = getTimezoneForAirport(iataCode, 'America/Mexico_City');
    }

    // Obtener componentes de fecha en la zona horaria específica
    const formatter = new Intl.DateTimeFormat('en-US', {
      timeZone: timezone,
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
    });

    const parts = formatter.formatToParts(date);
    const year = parseInt(parts.find(p => p.type === 'year')?.value || '0');
    const month = parseInt(parts.find(p => p.type === 'month')?.value || '1') - 1;
    const day = parseInt(parts.find(p => p.type === 'day')?.value || '1');

    // Crear fecha local usando estos componentes
    return new Date(year, month, day);
  };

  // Combinar y ordenar todos los eventos cronológicamente
  const getAllEvents = () => {
    const events: Array<{
      id: string;
      type:
        | "accommodation"
        | "activity"
        | "flight"
        | "transport"
        | "cruise"
        | "note";
      date: Date;
      data: any;
    }> = [];

    // Agregar actividades
    activities.forEach((activity) => {
      events.push({
        id: activity.id,
        type: "activity",
        date: new Date(activity.date),
        data: activity,
      });
    });

    // Agregar vuelos - usar fecha original para ordenar, zona horaria se usa solo para agrupar
    flights.forEach((flight) => {
      events.push({
        id: flight.id,
        type: "flight",
        date: new Date(flight.departureDate),
        data: flight,
      });
    });

    // Agregar transportes
    transports.forEach((transport) => {
      events.push({
        id: transport.id,
        type: "transport",
        date: new Date(transport.pickupDate),
        data: transport,
      });
    });

    // Agregar cruceros
    cruises.forEach((cruise) => {
      events.push({
        id: cruise.id,
        type: "cruise",
        date: new Date(cruise.departureDate),
        data: cruise,
      });
    });

    // Agregar alojamientos
    accommodations.forEach((accommodation) => {
      events.push({
        id: accommodation.id,
        type: "accommodation",
        date: new Date(accommodation.checkIn),
        data: accommodation,
      });
    });

    // Agregar notas importantes visibles para viajeros
    notes
      .filter((note) => note.visibleToTravelers)
      .forEach((note) => {
        events.push({
          id: note.id,
          type: "note",
          date: new Date(note.noteDate),
          data: note,
        });
      });

    // Ordenar cronológicamente
    return events.sort((a, b) => a.date.getTime() - b.date.getTime());
  };

  const chronologicalEvents = getAllEvents();

  const formatPrice = (price: number): string => {
    return price.toLocaleString("es-MX", {
      style: "currency",
      currency: "MXN",
    });
  };

  // Agrupar eventos por día
  /*const groupEventsByDay = (events: any[]) => {
    const groups: { [key: string]: any[] } = {};

    console.info("Events:", events);

    events.forEach((event) => {
      const date = new Date(event.date); // Forzar UTC
      const dayKey = date.toISOString().split("T")[0]; // YYYY-MM-DD format  // no usar

      console.info("Event Date:", event.date);
      console.info("Day Key:", dayKey);


      if (!groups[dayKey]) {
        groups[dayKey] = [];
      }
      groups[dayKey].push(event);
    });

    return Object.keys(groups)
      .sort()
      .map((dateKey) => ({
        date: new Date(dateKey),
        events: groups[dateKey],
      }));
  };*/

  const groupEventsByDay = (events: any[]) => {
    const groups: { [key: string]: any[] } = {};

    events.forEach((event) => {
      const d = new Date(event.date); // Puede ser string o Date

      let year: number, month: string, day: string;

      // Para vuelos, usar la zona horaria guardada o detectada para determinar el día local
      if (event.type === 'flight') {
        const flight = event.data;
        const localDate = getDateInTimezone(
          event.date,
          flight.departureCity,
          flight.departureTimezone
        );
        year = localDate.getFullYear();
        month = String(localDate.getMonth() + 1).padStart(2, "0");
        day = String(localDate.getDate()).padStart(2, "0");
      } else {
        // Para otros eventos, usar la fecha local del navegador
        year = d.getFullYear();
        month = String(d.getMonth() + 1).padStart(2, "0");
        day = String(d.getDate()).padStart(2, "0");
      }

      const dayKey = `${year}-${month}-${day}`; // YYYY-MM-DD local

      if (!groups[dayKey]) groups[dayKey] = [];
      groups[dayKey].push(event);
    });

    return Object.keys(groups)
      .sort()
      .map((dateKey) => {
        const [y, m, day] = dateKey.split("-").map(Number);

        // 👇 medianoche local (NO UTC)
        const groupDate = new Date(y, m - 1, day);

        // Ordenar eventos dentro del día por su hora UTC real
        groups[dateKey].sort(
          (a, b) => new Date(a.date).getTime() - new Date(b.date).getTime(),
        );

        return {
          date: groupDate,
          events: groups[dateKey],
        };
      });
  };

  const formatDayLabel = (date: Date): string => {
    const dayNames = [
      "domingo",
      "lunes",
      "martes",
      "miércoles",
      "jueves",
      "viernes",
      "sábado",
    ];

    const monthNames = [
      "enero",
      "febrero",
      "marzo",
      "abril",
      "mayo",
      "junio",
      "julio",
      "agosto",
      "septiembre",
      "octubre",
      "noviembre",
      "diciembre",
    ];

    const dayName = dayNames[date.getDay()];
    const dayNumber = date.getDate();
    const monthName = monthNames[date.getMonth()];
    const year = date.getFullYear();

    return `${capitalize(dayName)} ${dayNumber} de ${monthName} de ${year}`;
  };

  const capitalize = (text: string): string =>
    text.charAt(0).toUpperCase() + text.slice(1);

  console.info(chronologicalEvents);
  const groupedEvents = groupEventsByDay(chronologicalEvents);

  console.info(groupedEvents);

  const renderEventCard = (event: any) => {
    switch (event.type) {
