              )}
              {event.data.arrivalTerminal && (
                <div>
                  <div className="font-medium text-gray-600 uppercase text-xs">
                    TERMINAL LLEGADA
                  </div>
                  <div className="text-gray-900">
                    {event.data.arrivalTerminal}
