    let cleanedNotes = notes
      .replace(/[-\s]*Tel:\s*[+]?[\d\s\-\(\)]+/gi, '')
      .replace(/[-\s]*Teléfono:\s*[+]?[\d\s\-\(\)]+/gi, '')
